import { useReducer } from 'react'
import CartContext from './cart-context'

const defaultCartState = {
    items: [],
    totalAmount: 0

}
const cartReducer = (state, action) => {

    if (action.type === 'ADD') {

        const itemIndex = state.items.findIndex( item => item.id === action.item.id );
        const existingCartItem = state.items[itemIndex];

        if(existingCartItem) {
            updatedItem = { ...existingCartItem, amount: existingCartItem.amount + action.item.amount };

        }
        //const updatedItem = state.items.concat(action.item);
       // const updateTotalAmount = state.totalAmount + action.item.price * action.item.amount
        return { items: updatedItem, totalAmount: updateTotalAmount }
    }

    if (action.type === 'REMOVE') {
        const updatedItem = state.items.concat(action.item);
        const updateTotalAmount = state.totalAmount + action.item.price * action.item.amount
        return { items: updatedItem, totalAmount: updateTotalAmount }
    }

    return defaultCartState;
}

const CartProvider = (props) => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);

    const addItemToCartHandler = (item) => {
        dispatchCartAction({ type: 'ADD', item: item })
    };
    const removeItemToCartHandler = (id) => {
        dispatchCartAction({ type: 'REMOVE', id: id })
    };

    const cartContext = {
        items: cartState.items,
        totalAmount: cartState.updateTotalAmount,
        addItem: addItemToCartHandler,
        removeItem: removeItemToCartHandler
    }

    return (
        <CartContext.Provider value={cartContext}>
            {props.children}
        </CartContext.Provider>
    )

}

export default CartProvider
